import React, {useState} from 'react';
import './App.css';
import {firebase} from './firebase';


function App() {
  //Constante para mostrar en el html las tareas
  const [tareas, setTareas] = useState([])
  //Constante para agregar la tarea
  const [tarea, setTarea] = useState('')
  //Constante para agregar la tarea
  const [fecha, setFecha] = useState('')

  React.useEffect(() => {
    const obtenerDatos = async () => {
      try {
        const db = firebase.firestore()
        const data = await db.collection('tareas').get()
        console.log(data.docs)
        const arrayData = await data.docs.map(doc => ({ id: doc.id, ...doc.data()}))
        console.log(arrayData)
        setTareas(arrayData)
      } catch (error) {
        console.log(error)
      }
    }

    obtenerDatos()

  }, [])

  //funcion agregar
  const agregar = async (e) => {
    e.preventDefault()

    //ValidaciÃ³n para saber si esta llena o vacia la tarea
    if(!tarea.trim() | !fecha.trim()){
      console.log("Falta un Campo")
      return
    }

    //Colocamos un try y catch por que la funcion es async
    //Vamos a invocar a db y firestore
    try {
      const db = firebase.firestore()
      //Declaramos un objeto para enviar todos los campos que enviaremos
      const nuevaTarea = {
        name: tarea,
        date: fecha
      }

      //Codigo para agregar la tarea en firebase codigo de plataforma
      const data = await db.collection('tareas').add(nuevaTarea)

      //Con esto actualizo la lista sin dar refres prueba antes de poner
      setTareas([
        ...tareas,
        {...nuevaTarea, id: data.id}
      ])      

      //Limpio tarea
      setTarea('')
      setFecha('')

    } catch (error) {
      console.log(error)
    }

    console.log(tarea)
  }

  return (
    <div className="container">
      <h1>Proyecto Firebase</h1>
      <div className="row">
        <div className="col-md-6">
            <h3>Lista de Tareas</h3>
            <ul className="list-group">
            {
                tareas.map(item => (
                  <li className="list-group-item" key={item.id}>
                  <span>{item.name}</span>
                  <br/>
                  <span>{item.date}</span>
                    <button className="btn btn-danger btn-sm float-right">
                        Eliminar
                    </button>
                    <button className="btn btn-warning btn-sm float-right mr-2">
                        Editar
                    </button>
                </li>
                ))
            }
            </ul>
        </div>
        <div className="col-md-6">
            <h3>Formulario</h3>
            <form onSubmit={agregar}>
              <input 
                type="text" 
                className="form-control mb-2"
                placeholder='Ingrese Tarea'
                value={tarea}
                onChange={e => setTarea(e.target.value)}
              />
              <input 
                type="date" 
                className="form-control mb-2"
                value={fecha}
                onChange={e => setFecha(e.target.value)}
              />
              <button
                type='submit'
                className="btn btn-dark btn-block btn-sm"
              >Agregar</button>
            </form>
        </div>
    </div>

    </div>
  );
}

export default App;
